import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ScrollView, TextInput } from 'react-native';

const Pokemon = () => {
  return (
    <ScrollView>
      <View>
        <Text style={styles.texto}>
          Snorlax Berrie
        </Text>

        <Image
          source={{ uri: 'https//pbs.twimg.com/media/E4sYypaVgAQ1Cv9.png' }}
        />
      </View>

    </ScrollView>
  );
}

const styles = StyleSheet.create({
  texto: {
    fontSize: 45,
    alignSelf: 'center',
    color: 'green'
  }
});
